mapper.insert:
insert into product_basket(
	product_no, userId, cnt, register_date, update_date
)values (
	#{product_no}, #{userId}, 1, now(), now()
)

mapper.update:
update product_basket
SET delete_yn = 0;
WHERE userId = #{userId} and product_no = #{product_no}

update product_basket
SET cnt = #{cnt} and update_date = now()
WHERE userId = #{userId} and product_no = #{product_no}
