mapper.select:
select 
	*
from comment
where product_no = #{search}
order by parent_no desc, no
limit #{pageStart}, #{pageEnd}

mapper.insert:
	insert into comment(
		comment, product_no, parent_no, writer, writer_id, register_date, update_date, delete_yn
	)values (
		#{comment}, #{product_no}, #{parent_no}, #{writer}, #{writer_id}, #{register_date}, #{update_date}, #{delete_yn}
    )
